namespace CourseApp.Tests.Module3
{
    using System;
    using System.Collections.Generic;
    using System.IO;
    using CourseApp.Module3;
    using Xunit;

    [Collection("Sequential")]
    public class RabinCarpTest : IDisposable
    {
        private const string Inp1 = @"ababbababa
aba
";

        private const string Out1 = @"0 5 7
";

        private const string Inp2 = @"
bbcccab
";

        private const string Out2 = @"287 1585 3612 4294 10800 12025 20604 26894
";

        public void Dispose()
        {
            var standardOut = new StreamWriter(Console.OpenStandardOutput());
            standardOut.AutoFlush = true;
            var standardIn = new StreamReader(Console.OpenStandardInput());
            Console.SetOut(standardOut);
            Console.SetIn(standardIn);
        }

        [Theory]
        [InlineData("AB", 3, 7, 1)]
        [InlineData("ABB", 3, 7, 4)]
        [InlineData("BBA", 3, 7, 5)]
        [InlineData("ABBA", 3, 7, 5)]
        [InlineData("aba", 257, 3571, 907)]
        [InlineData("bab", 257, 3571, 2422)]
        public void TestHash1(string input, ulong x, ulong p, ulong exp)
        {
            var res = RabinCarp.CalculateHash(input, x, p);
            Assert.Equal(exp, res);
        }

        [Theory]
        [InlineData("ABB", 'A', 4, 3, 7, 5)]
        [InlineData("aba", 'b', 907, 257, 3571, 2422)]
        [InlineData("bab", 'a', 2422, 257, 3571, 907)]
        public void TestSlidingHash1(string input, char append, ulong hash, ulong x, ulong p, ulong exp)
        {
            ulong multiplier = 1;
            for (int i = 1; i < input.Length; i++)
            {
                multiplier *= x;
                multiplier %= p;
            }

            var res = RabinCarp.SlidingHash(input, append, hash, x, p, multiplier);
            Assert.Equal(exp, res);
        }

        [Theory]
        [InlineData(Inp1, Out1)]
        [InlineData(Inp2, Out2)]
        public void Test1(string input, string expected)
        {
            var stringWriter = new StringWriter();
            Console.SetOut(stringWriter);

            var stringReader = new StringReader(input);
            Console.SetIn(stringReader);

            // act
            RabinCarp.RabinCarpMethod();

            // assert
            var output = stringWriter.ToString();

            // var result = string.Join(Environment.NewLine, output);
            Assert.Equal($"{expected}", output);
        }
    }
}